'use strict';

var nowUtc = require('../../utils').get_now_utc;
function dateUtc() { return new Date(nowUtc()); }

module.exports = function genTranscodingSchema() {
    return {

        // see the zencoder file to get the possible values
        transcoding_status: {
            type: Number,
            default: 0
        },

        transcoding_job_id: Number,

        transcoding_oplogs: [{
            ts: {
                type: Date,
                default: dateUtc
            },
            comment: String,
            error: String,
            _id: false
        }]

    }
}
