'use strict';

module.exports = function genWebHooksSchema() {
    return [{
        // id of the user who register the web hook,
        // multiple users can register a web hook on the
        // same resource, so this is required to keep track of it
        subscriber: {
            type: String,
            index: true
        },
        uri: String,

        // To differenciate different hooks saved on the same resource
        // (eg: player and event are persisted on the player object)
        original_resource: String,
        _id: false
    }]
}
