/**
 * Module dependencies.
 */

/**
 * Exports schema
 */
module.exports.load = function(mg) {
	var Schema = mg.Schema;
	var schema = new Schema({
		'date' : {
			type : Date,
			index : true
		},
		'ds' : {
			type : Schema.Types.ObjectId,
			ref : 'billboard',
			index : {
				unique : false
			}
		},
		'available_percentage' :{
			type : Number,
			'default' : 0,
		},
		'is_stable' : {
			type : Boolean,
			'default' : true  
		}, 
		
		'connection_hst' : [ {
			"ts" : Date,
			"status" : {
				type : Boolean,
				'default' : true  // Is connected
			}
		} ]
	});
	return schema;
};
