'use strict';
var _ = require('lodash');
var fullAddress = require('./fullAddress');

module.exports.load = function loadLocationSchema (mongoose) {
    var Schema = mongoose.Schema;

    var location = new Schema(_.assign({}, fullAddress, {
        name: String,
        tags: [ String ],
        contactinfo: String,
        floorplan: {
            type: Schema.Types.ObjectId,
            ref: 'floorplans'
        },
        entity: {
            type: Schema.Types.ObjectId,
            ref: 'entities'
        },
        ts: {
            type: Date,
            default: Date.now
        },
        ddate: Date
    }));

    return location;
}
