/**
 * Exports schema
 */

module.exports.load = function(mg) {
    var Schema = mg.Schema;
    var template = new Schema({
        "ts" : {
            type : Date,
            'default' : Date.now
        },
        "cdate" : {
            type : Date,
            'default' : Date.now
        },
        "desc" : {
            type : String
        },
        "type" : {
            type : String
        },
        "subtype" : {
            type : String
        },
        "lan" : {
            type : String
        }, //ISO 639-3
        "subject" : {
            type : String
        },
        "body" : [{
            "type" : {
                type : String // HTML, Long TEXT, Short Text(usually has 70 bytes limitation) 
            }, 
            "val" : {
                type : String // could be URL or Whole string
            }
        }]
    });
    return template;
};
