/**
 * Exports schema
 */
module.exports.load = function(mg) {
	var Schema = mg.Schema;
	var schema = new Schema({
        "cdate" : {
            type : Date,
            'default' : Date.now
        },
        "ts" : {
            type : Date,
            'default' : Date.now
        },
        "role" : {
            type : Schema.Types.ObjectId,
            ref : "roles"
        },
        "rights" : { // CRUD
            type : String,
            'default' : ""
        },
        "rtype" : { // resource type
            type : Number,
        },
        "rid" : { // resource id
            type : Schema.Types.ObjectId,
        },
        "all_doc" : {
            type : Boolean,
            'default' : false
        },
        "entity" : {
            type : Schema.Types.ObjectId,
            ref : "entities"
        }
	});

	return schema;
};
