'use strict';

module.exports.load = function(mongoose) {
    var Schema = mongoose.Schema;

    var signTypes = new Schema({
        name: [{
            lan: String, //ISO 639-3
            name: {
                type: String,
                trim: true
            }
        }],
        desc: [{
            lan: String, //ISO 639-3
            desc: {
                type: String,
                trim: true
            }
        }],
        ts: {
            type: Date,
            default: Date.now
        },
        ddate: Date,
        entity: {
            type: Schema.Types.ObjectId,
            ref: 'entities'
        },
        size: Number,
        orientation: String,
        arangement: { //description of the grid
            w: Number,
            h: Number
        },
        aspectratio: String
    });

    return signTypes;
};
