module.exports.load = function(mg) {
	var Schema = mg.Schema;
	var contact = new Schema({
		"cdate" : {
			type : Date,
			'default' : Date.now
		},
		'ddate' : Date,
		"stat" : {
			type : Number,
			'default' : "0"
		},
		"nickname" : {
			type : String,
			trim : true
		},
		"email" : String,
		"user" : {
			type : Schema.Types.ObjectId,
			ref : 'userprofile'
		}
	});

	var contacts = new Schema({
		"user" : {
			type : Schema.Types.ObjectId,
			ref : 'userprofile',
			index : {
				unique : true
			}
		},
		"contact" : [ contact ]
	});

	return contacts;
};