/**
 * user.profile wrapper
 */

/**
 * Exports schema
 */

module.exports.load = function(mg) {
    var Schema = mg.Schema;
    var socials = new Schema({
        "user" : {
            type : Schema.Types.ObjectId,
            ref : 'userprofile',
            index : {
                unique : true
            }
        },
        "ts" : {
            type : Date,
            'default' : Date.now
        },
        "google" : {
            "oauth" : {
                "access_token" : String,
                "refresh_token" : String
            }
        }
    });

    return socials;
};
