/**
 * Exports schema
 */

module.exports.load = function(mg) {
	var Schema = mg.Schema;
	var Log = new Schema({
		"cdate" : {
			type : Date,
			'default' : Date.now
		},
		"ds" : {
			type : Schema.Types.ObjectId,
			ref : 'billboard'
		},
		"campaign" : {
			type : Schema.Types.ObjectId,
			ref : 'campaign'
		},
		"layout" : {
			type : Schema.Types.ObjectId,
			ref : 'layout'
		},
		"widget" : {
			type : Schema.Types.ObjectId,
			ref : 'widget'
		},
		"widget_name" : {
			type : String,
			'default' : "skm"
		},
		"time" : {
			type : Date,
			'default' : Date.now
		},
		"action" : {
			type : String
		},
		"target" : {
			type : String
		},
		"comment" : {
			type : String
		},
		"url" : {
			type : String
		}
	});
	return Log;
};